class Repository{constructor(n){this.controller=n}httpGet(n,t){return new Promise((i,r)=>{$.ajax({method:"GET",url:`/${this.controller}/${n}`,data:t,dataType:"JSON",success:i,error:r})})}httpPost(n,t){return new Promise((i,r)=>{$.ajax({method:"POST",url:`/${this.controller}/${n}`,data:t,dataType:"JSON",success:i,error:r})})}}